// ProfilesPage.js

import React from 'react';
import { Link } from 'react-router-dom';
import defaultImage from './default.jpeg';
import '../App.css';
import data from './data'; // Import the data array

const ProfilesPage = () => {
  const renderProfileItems = () => {
    return data.map((profile, index) => (
      <div key={index} className="portfolio-item">
        <Link to={`/profiles/${profile.Name}`} className="buttonProfile">
          {/* Use the Image from the data or the defaultImage if not available */}
          <img src={profile.Image || defaultImage} alt={`Profile of ${profile.Name}`} />
          <span className="username">{profile.Name}</span>
        </Link>
      </div>
    ));
  };

  return (
    <div>
      <div className="container">
        <div className="portfolio-grid">{renderProfileItems()}</div>
      </div>
    </div>
  );
};

export default ProfilesPage;
