import React from 'react';
import { useSpring, animated } from 'react-spring';
import './AnimatedIntroduction.css'; // Import your CSS file for styling

const useWordAnimation = (word, index) => {
  return useSpring({
    opacity: 1,
    transform: 'translateY(0px)',
    from: { opacity: 0, transform: 'translateY(20px)' },
    delay: index * 100, // Adjust the delay to control the timing of each word
  });
};

const AnimatedIntroduction = () => {
  const text = [
    "Portfolionaire,",
    "our",
    "dedicated",
    "resume",
    "website,",
    "is",
    "a",
    "dynamic",
    "tool",
    "catering",
    "specifically",
    "to",
    "the",
    "students",
    "of",
    "our",
    "class."
    // Add more words as needed
  ];

  const wordAnimations = text.map((word, index) => {
    const props = useWordAnimation(word, index);

    return (
      <animated.span key={index} style={props}>
        {word}{' '}
      </animated.span>
    );
  });

  return <div className="animated-intro-container">{wordAnimations}</div>;
};

export default AnimatedIntroduction;
